function levusDropdown(){document.querySelectorAll(".levus-dropdown").forEach(e=>e.addEventListener("mousedown",function(){setTimeout(()=>{this.querySelector("ul").classList.add("open")},300)})),document.querySelector("html").addEventListener("click",()=>{document.querySelectorAll(".levus-dropdown ul").forEach(e=>e.classList.remove("open"))})}levusDropdown();
